window.onload=()=>{class Tooltip{constructor(t,n){this.element=t,this.props=n;const o=this.props.label.length,i=Math.random().toString(36).substring(2,6);let s=`<span class = "label-${i}">${this.props.label}</span>\n<style>\n    .tooltip{\n        position: relative;\n        display: inline-block;\n    }\n    .tooltip .label-${i}{\n        cursor: pointer;\n        visibility: hidden;\n        opacity: 0;\n        text-align: center;\n        font-size: 10pt;\n        width: ${o}ex;\n        position: absolute;\n        background-color: #555;\n        color: white;\n        border-radius: 5px;\n        padding: 5px 8px;\n        left: 50%;\n        ${"top"==this.props.position?"bottom":"top"}: 170%;\n        margin-left: -${o/2+1}ex;\n        transition: 0.3s;\n        z-index: 1000 !important;\n    }\n    .tooltip .label-${i}::after{\n        content: "";\n        position: absolute;\n        ${"top"==this.props.position?"bottom":"top"}: -50%;\n        left: 50%;\n        margin-${"top"==this.props.position?"bottom":"top"}: 3px;\n        margin-left: -5px;\n        border-width: 5px;\n        border-style: solid;\n        border-color: ${"top"==this.props.position?"#555":"transparent"} transparent ${"top"==this.props.position?"transparent":"#555"} transparent;\n    }\n    .tooltip:hover > .label-${i}{\n        visibility: visible;\n        ${"top"==this.props.position?"bottom":"top"}: 150%;\n        opacity: 1;\n    }\n</style>`;this.element.insertAdjacentHTML("beforeend",s),this.element.classList.add("tooltip")}}}